import React, { Component } from "react";
import { Button, FormGroup, FormControl } from "react-bootstrap";
import "./Login.css";

export default class Signup extends Component {
  constructor(props) {
    super(props);

    this.state = {
      isLoading: true,
      username: "",
      password: "",
      signUpError:""
    };
  }
  onSignup(){
    const {
      username,
      password
    } = this.state
    this.setState({
      isLoading: true,
    });
    fetch("http://localhost:8766/user/signup", {
      method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          username: username,
          password: password
        }),
      }).then(res => res.json())
      .then(json => {
        console.log('json', json);
        if (json.success) {
          this.setState({
            signUpError: json.message,
            username: '',
            password: '',
          });
        } else {
          this.setState({
            signUpError: json.message,
            isLoading: false,
          });
        }
      });
    }

  render() {
    const {
      username,
      password,
      signUpError
    } = this.state
    return (
      <div>
            {
              (signUpError) ? (
                <p>{signUpError}</p>
              ) : (null)
            }
            <p>Sign Up</p>
            <input
              type="email"
              placeholder="Email"
              value={username}
              onChange={this.onTextboxChangeSignUpEmail}
            /><br />
            <input
              type="password"
              placeholder="Password"
              value={password}
              onChange={this.onTextboxChangeSignUpPassword}
            /><br />
            <button onClick={this.onSignUp}>Sign Up</button>
          </div>

    );
  }
}
