import React, { Component } from 'react';

export default class SignUp extends Component {
  constructor(props){
    super(props)
    this.state={
      username: undefined,
      password: undefined,
      signUpError: undefined
    }
    this.handleChangeUsername = this.handleChangeUsername.bind(this)
    this.handleChangePassword = this.handleChangePassword.bind(this)
    this.onSignUp = this.onSignUp.bind(this)
  }
  handleChangeUsername(value){
    this.setState({
      username: value,
    })
  }
  handleChangePassword(value){
    this.setState({
      password: value,
    })
  }
  onSignUp() {
    const {
      username,
      password
    } = this.state;
    fetch("http://localhost:8766/user/signup",{
      method: 'POST',
      mode: 'cors',
      credentials: 'same-origin',
      headers: {
        'Content-Type': 'application/json',
        'Access-Control-Allow-Origin': '*',
        'Access-Control-Allow-Headers': 'Content-Type',
        'Access-Control-Allow-Methods': 'POST',
        'Access-Control-Allow-Credentials':'true'
      },
      body: JSON.stringify({
        username:username,
        password:password
      }),
    }).then((res)=>res.json()).then((result)=>{
      if (result.Status==="Success")
        console.log("OK")
      else
        alert("Have problem")
    })
  }
  render() {
    return (
    <form className="form-signin">
            <h2 className="form-signin-heading"> Please sign in </h2>
            <label className="sr-only"> Username
            </label>
            <input id="username" className="form-control" placeholder="Username" onChange={(e)=>this.handleChangeUsername(e.target.value)} required />
            <label className="sr-only"> Password</label>
            <input type="password" id="password" className="form-control" placeholder="Password" onChange={(e)=>this.handleChangePassword(e.target.value)} required />
            <button className="btn btn-lg btn-primary btn-block" type="button" onClick={this.onSignUp}> Sign in
            </button>
        </form>
    )
}
}
