import React, { Component } from 'react';

export default class SignUp extends Component {
  constructor(props){
    super(props)
    this.state={
      username: undefined,
      password: undefined,
      signUpError: undefined
    }
    this.handleChangeUsername = this.handleChangeUsername.bind(this)
    this.handleChangePassword = this.handleChangePassword.bind(this)
    this.handleSignUp = this.handleSignUp.bind(this)
  }
  handleChangeUsername(value){
    this.setState({
      username: value,
    })
  }
  handleChangePassword(value){
    this.setState({
      password: value,
    })
  }
  handleSignUp() {
    const {
      username,
      password,
      passwordConfirm
    } = this.state;
    fetch("http://localhost:8766/user/signup",{
      method: 'POST',
      mode: 'cors',
      credentials: 'same-origin',
      headers: {
        'Content-Type': 'application/json',
        'Access-Control-Allow-Origin': '*',
        'Access-Control-Allow-Headers': 'Content-Type',
        'Access-Control-Allow-Methods': 'POST',
        'Access-Control-Allow-Credentials':'true'
      },
      body: JSON.stringify({
        username:username,
        password:password,
        passwordConfirm:passwordConfirm
      }),
    }).then((res)=>res.json()).then((result)=>{

      if (result.message==="success")
        alert("Created " + username + " successfully")
      else
        alert("User "+ username +" is existing. Please choose other username")
    })
  this.setState({
    username:"",
    password:""
  })
  }
  render() {
    return (
    <form className="form-signin">
            <h2 className="form-signin-heading"> Please sign in </h2>
            <label className="sr-only"> Username
            </label>
            <input id="username" className="form-control" placeholder="Username" value={this.state.username} onChange={(e)=>this.handleChangeUsername(e.target.value)} required />

            <label className="sr-only"> Password</label>
            <input type="password" id="password" className="form-control"  value={this.state.password} placeholder="Password" onChange={(e)=>this.handleChangePassword(e.target.value)} required />
            <label className="sr-only"> Password Comfirm</label>
            <input type="password" id="password-confirm" className="form-control" value={this.state.password} placeholder="Password Comfirm" onChange={(e)=>this.handleChangePassword(e.target.value)} required />
            <button className="btn btn-lg btn-primary btn-block" type="button" onClick={this.handleSignUp}> Sign in
            </button>
        </form>
    )
}
}
